import React, { useState, useRef } from "react";
import axios from "axios";
import "./App.css";
import Camera from "./components/Camera";

function App() {
  // --- Stateler (Durum Y√∂netimi) ---
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [uploadResult, setUploadResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showCamera, setShowCamera] = useState(false);
  const [pdfInfo, setPdfInfo] = useState(null);
  const [isGeneratingPDF, setIsGeneratingPDF] = useState(false);
  const [showEnhancedText, setShowEnhancedText] = useState(false);
  const [isEnhancing, setIsEnhancing] = useState(false);

  const fileInputRef = useRef();

  // --- Fonksiyonlar (Aynƒ± Mantƒ±k, Temiz Kod) ---
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setUploadResult(null);
      const fileReader = new FileReader();
      fileReader.onload = () => setPreviewUrl(fileReader.result);
      fileReader.readAsDataURL(file);
    }
  };

  const handleCameraCapture = (file, previewData) => {
    setSelectedFile(file);
    setPreviewUrl(previewData);
    setUploadResult(null);
    setShowCamera(false);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    setIsLoading(true);
    const formData = new FormData();
    formData.append("document", selectedFile);

    try {
      const response = await axios.post("http://localhost:5000/api/upload", formData);
      setUploadResult(response.data);
    } catch (error) {
      alert("Upload failed: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const enhanceText = async () => {
    if (!uploadResult?.ocr?.text) return;
    setIsEnhancing(true);
    try {
      const response = await axios.post("http://localhost:5000/api/enhance-text", {
        text: uploadResult.ocr.text
      });
      setUploadResult(prev => ({ ...prev, enhancement: response.data.enhancement }));
      setShowEnhancedText(true);
    } catch (error) {
      alert("Enhancement failed");
    } finally {
      setIsEnhancing(false);
    }
  };

  const generatePDF = async () => {
    setIsGeneratingPDF(true);
    try {
      const response = await axios.post('http://localhost:5000/api/generate-pdf', {
        ocrText: uploadResult.ocr.text,
        title: `Scanned Doc`
      });
      setPdfInfo(response.data.pdf);
    } catch (error) {
      alert('PDF Error');
    } finally {
      setIsGeneratingPDF(false);
    }
  };

  // --- Aray√ºz (JSX) ---
  return (
    <div className="App">
      <header className="App-header">
        <h1>DocScanner AI</h1>
        <p>Analyze, extract text and convert documents to PDF</p>
      </header>

      <main className="main-content">
        {/* Adƒ±m 1: Dosya Se√ßimi */}
        <section className="card">
          <h3 style={{ marginBottom: '1rem', textAlign: 'center' }}>Step 1: Upload or Capture</h3>
          <input 
            type="file" 
            ref={fileInputRef} 
            onChange={handleFileSelect} 
            accept="image/*" 
            style={{ display: "none" }} 
          />
          <div className="button-group">
            <button className="btn btn-primary" onClick={() => fileInputRef.current.click()}>
              üìÅ Choose File
            </button>
            <button className="btn btn-secondary" onClick={() => setShowCamera(true)}>
              üì∑ Take Photo
            </button>
          </div>
          {selectedFile && !uploadResult && (
            <div style={{ textAlign: 'center', marginTop: '1rem', color: '#64748b', fontSize: '0.9rem' }}>
              Selected: <strong>{selectedFile.name}</strong>
            </div>
          )}
        </section>

        {/* Adƒ±m 2: √ñnizleme ve Tarama */}
        {previewUrl && !uploadResult && (
          <section className="card preview-section">
            <h3>Step 2: Review and Scan</h3>
            <div className="image-container">
              <img src={previewUrl} alt="Preview" className="preview-image" />
            </div>
            <button 
              className={`btn btn-primary ${isLoading ? 'loading' : ''}`} 
              onClick={handleUpload} 
              disabled={isLoading}
              style={{ width: '100%', maxWidth: '300px' }}
            >
              {isLoading ? "üîç Scanning..." : "üöÄ Start AI Scan"}
            </button>
          </section>
        )}

        {/* Adƒ±m 3: Sonu√ßlar */}
        {uploadResult && (
          <div className="results-grid">
            {/* Sol Kart: OCR ve Metin */}
            <div className="card">
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                <h3>Extracted Text</h3>
                <button className="btn btn-secondary" style={{ padding: '4px 12px' }} onClick={enhanceText} disabled={isEnhancing}>
                  {isEnhancing ? '...' : '‚ú® Improve'}
                </button>
              </div>
              <div className="extracted-text-area">
                {showEnhancedText && uploadResult.enhancement 
                  ? uploadResult.enhancement.enhanced 
                  : uploadResult.ocr?.text || "No text detected."}
              </div>
            </div>

            {/* Saƒü Kart: Analiz ve PDF */}
            <div className="card">
              <h3>Document Info</h3>
              <hr style={{ margin: '1rem 0', opacity: 0.1 }} />
              <p><strong>Confidence:</strong> <span className="stat-badge success-badge">{uploadResult.ocr?.confidence}%</span></p>
              <p style={{ marginTop: '0.5rem' }}><strong>Quality:</strong> {uploadResult.analysis?.quality.score} / 100</p>

              <div style={{ marginTop: '2rem' }}>
                <h4>Actions</h4>
                <button className="btn btn-primary" style={{ width: '100%', marginTop: '0.5rem' }} onClick={generatePDF} disabled={isGeneratingPDF}>
                  {isGeneratingPDF ? '‚è≥ Generating...' : 'üìÑ Download PDF'}
                </button>
                {pdfInfo && (
                  <button className="btn btn-secondary" style={{ width: '100%', marginTop: '0.5rem' }} onClick={() => window.open(`http://localhost:5000${pdfInfo.pdfUrl}`)}>
                    üì• Click to Download
                  </button>
                )}
              </div>
            </div>
          </div>
        )}
      </main>

      {showCamera && (
        <Camera onCapture={handleCameraCapture} onClose={() => setShowCamera(false)} />
      )}
    </div>
  );
}

export default App;
