import React, {useState , useRef} from "react";
import axios from "axios";
import "./App.css";
import Camera from "./components/Camera";

function App() {

  const [selectedFile , setSelectedFile] = useState(null);
  const [previewUrl , setPreviewUrl] = useState(null);
  const [uploadResult , setUploadResult] = useState(null);
  const [isLoading , setIsLoading] = useState(false);
  const [showCamera , setShowCamera] = useState(false);
  const [pdfInfo , setPdfInfo] = useState(null);
  const [isGeneratingPDF , setIsGeneratingPDF] = useState(false);
  const [showEnhancedText, setShowEnhancedText] = useState(false);
  const [isEnhancing , setIsEnhancing] = useState(false);

  const fileInputRef = useRef();

  // Handle file selection (dosya se√ßildiƒüinde √ßalƒ±≈üacak fonksiyon:)

  const handleFileSelect = (event) => {

    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setUploadResult(null);

      // Dosya √∂nizlemesi i√ßin URL ol≈üulturma:
      const fileReader = new FileReader();
      fileReader.onload = () => {
        setPreviewUrl(fileReader.result);
      };
      fileReader.readAsDataURL(file);


    }
  };

  const handleCameraCapture = (file , previewData) => {
    setSelectedFile(file);
    setPreviewUrl(previewData);
    setUploadResult(null);
    setShowCamera(false); //Kamerayƒ± kapat
  }

  //Dosya y√ºkleme butonƒ±na tƒ±klanƒ±nca √ßalƒ±≈üacak fonksiyon:
  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file first.");
      return;
    }

    setIsLoading(true);
    setUploadResult(null);

    //Backende dosya g√∂ndermek i√ßin formData kullanma:
    const formData = new FormData();
    formData.append("document" , selectedFile);

    try {

      console.log("Uploading file...", selectedFile.name);

      //Axios ile backend APIsine POST isteƒüi g√∂nderme:
      const response = await axios.post("http://localhost:5000/api/upload" , formData , {
        headers: {
          "Content-Type" : "multipart/form-data"
        },
      });


      setUploadResult(response.data);
      console.log("Upload successful:", response.data);

    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadResult({
        success: false,
        error: "Error uploading file" + error.message
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Dosya se√ßme butonuna tƒ±klanƒ±nca √ßalƒ±≈üacak fonksiyon:
  const handleSelectClick = () => {
    fileInputRef.current.click();
  };

  //Kamera A√ßma butonu:
  const handleCameraClick = () => {
    setShowCamera(true);
  };

  //Metin Iyile≈ütirme Fonksiyonu:
  const enhanceText = async () => {
  if (!uploadResult || !uploadResult.ocr || !uploadResult.ocr.text) {
    alert("Please upload a file and complete OCR first");
    return;
  }

  setIsEnhancing(true);

  try {
    const response = await axios.post("http://localhost:5000/api/enhance-text", {
      text: uploadResult.ocr.text
    });

    const enhancement = response.data.enhancement;

    // Eƒüer metin zaten temizse kullanƒ±cƒ±ya d√ºr√ºst ol
    if (!enhancement || enhancement.enhanced === uploadResult.ocr.text) {
      alert("Text is already clean. No improvement needed.");
      setIsEnhancing(false);
      return;
    }

    // State'e kaydet
    setUploadResult(prev => ({
      ...prev,
      enhancement
    }));

    setShowEnhancedText(true);

  } catch (error) {
    console.error("Text enhancement error:", error);
    alert("Text enhancement failed: " + error.message);
  } finally {
    setIsEnhancing(false);
  }
};


  //Orijinal ve iyile≈ütirilmi≈ü metin arasƒ± ge√ßi≈ü. 
  const toggleTextVersion = () => {
  setShowEnhancedText(!showEnhancedText);
  };

  // PDF Olu≈üturma fonksiyonu:
  const generatePDF = async (type = 'simple') => {
    if (!uploadResult || !uploadResult.ocr || !uploadResult.ocr.text) {
      alert('Please upload a file and complete OCR process first.');
      return;
    }

    setIsGeneratingPDF(true);

    try {
      let response;

      if (type === 'simple') {
        // Simple PDF
        response = await axios.post('http://localhost:5000/api/generate-pdf', {
          ocrText: uploadResult.ocr.text,
          title: `Scanned: ${uploadResult.file.originalname}`
        });
      }


      setPdfInfo(response.data.pdf);
      console.log('PDF created:', response.data.pdf);

    } catch (error) {
      console.error('PDF generation error:', error);
      alert('PDF creation error: ' + error.message);
    } finally {
      setIsGeneratingPDF(false);
    }
  };

  // PDF download function
  const downloadPDF = () => {
    if (pdfInfo && pdfInfo.pdfUrl) {
      window.open(`http://localhost:5000${pdfInfo.pdfUrl}`, '_blank');
    }
  };

  return (
  <div className="app-container">
    {/* √úst Ba≈ülƒ±k - Daha sade ve profesyonel */}
    <header className="main-header">
      <div className="header-content">
        <h1>DocScanner <span className="badge">AI-Powered</span></h1>
        <p>Transform your images into searchable PDFs instantly</p>
      </div>
    </header>

    <main className="content-wrapper">
      {/* 1. Aksiyon Kartƒ±: Dosya Se√ßme ve Kamera */}
      <section className="card upload-card">
        <div className="upload-options">
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileSelect}
            accept="image/*"
            style={{ display: "none" }}
          />
          <button className="btn btn-primary" onClick={handleSelectClick}>
            <span>üìÅ Choose Document</span>
          </button>
          <button className="btn btn-secondary" onClick={handleCameraClick}>
            <span>üì∑ Open Camera</span>
          </button>
        </div>

        {selectedFile && !uploadResult && (
          <div className="selected-file-badge">
            <span className="file-name">{selectedFile.name}</span>
            <span className="file-size">({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)</span>
          </div>
        )}
      </section>

      {/* 2. √ñnizleme ve Y√ºkleme Butonu */}
      {previewUrl && !uploadResult && (
        <section className="card preview-card">
          <div className="preview-header">
            <h3>Document Preview</h3>
          </div>
          <div className="image-frame">
            <img src={previewUrl} alt="Preview" />
          </div>
          <button 
            className={`btn btn-upload ${isLoading ? 'loading' : ''}`}
            onClick={handleUpload}
            disabled={isLoading}
          >
            {isLoading ? "Processing..." : "Start Scanning"}
          </button>
        </section>
      )}

      {/* 3. Sonu√ßlar Paneli - Karma≈üayƒ± √∂nlemek i√ßin ƒ±zgara yapƒ±sƒ± */}
      {uploadResult && (
        <div className="results-grid">
          {/* Sol Kolon: G√∂r√ºnt√º Analizi ve OCR */}
          <div className="results-main">
            <div className="card result-card">
              <div className="card-header">
                <h3>Extracted Text</h3>
                <div className="header-actions">
                  <button className="btn-sm btn-outline" onClick={enhanceText} disabled={isEnhancing}>
                    {isEnhancing ? '...' : '‚ú® Enhance'}
                  </button>
                  {uploadResult.enhancement && (
                     <button className="btn-sm btn-outline" onClick={toggleTextVersion}>
                        {showEnhancedText ? 'Original' : 'Enhanced'}
                     </button>
                  )}
                </div>
              </div>

              <div className="text-display-area">
                <pre>
                  {showEnhancedText && uploadResult.enhancement 
                    ? uploadResult.enhancement.enhanced 
                    : uploadResult.ocr?.text}
                </pre>
              </div>
            </div>
          </div>

          {/* Saƒü Kolon: ƒ∞statistikler ve Export */}
          <aside className="results-sidebar">
            <div className="card stats-card">
              <h4>Scan Quality</h4>
              <div className="stat-row">
                <span>Confidence:</span>
                <span className="stat-value">{uploadResult.ocr?.confidence}%</span>
              </div>
              <div className="stat-row">
                <span>Brightness:</span>
                <span className="stat-value">{uploadResult.analysis?.quality.brightness}</span>
              </div>
            </div>

            <div className="card export-card">
              <h4>Actions</h4>
              <button className="btn btn-pdf" onClick={() => generatePDF('simple')}>
                {isGeneratingPDF ? 'Generating...' : 'Download PDF'}
              </button>
            </div>
          </aside>
        </div>
      )}
    </main>

    {showCamera && (
      <Camera onCapture={handleCameraCapture} onClose={() => setShowCamera(false)} />
    )}
  </div>
);

  }

  export default App;
