import React, {useState , useRef} from "react";
import axios from "axios";
import "./App.css";
import Camera from "./components/Camera";

function App() {

  const [selectedFile , setSelectedFile] = useState(null);
  const [previewUrl , setPreviewUrl] = useState(null);
  const [uploadResult , setUploadResult] = useState(null);
  const [isLoading , setIsLoading] = useState(false);
  const [showCamera , setShowCamera] = useState(false);
  const [pdfInfo , setPdfInfo] = useState(null);
  const [isGeneratingPDF , setIsGeneratingPDF] = useState(false);
  const [showEnhancedText, setShowEnhancedText] = useState(false);
  const [isEnhancing , setIsEnhancing] = useState(false);

  const fileInputRef = useRef();

  // Handle file selection (dosya se√ßildiƒüinde √ßalƒ±≈üacak fonksiyon:)

  const handleFileSelect = (event) => {

    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setUploadResult(null);

      // Dosya √∂nizlemesi i√ßin URL ol≈üulturma:
      const fileReader = new FileReader();
      fileReader.onload = () => {
        setPreviewUrl(fileReader.result);
      };
      fileReader.readAsDataURL(file);


    }
  };

  const handleCameraCapture = (file , previewData) => {
    setSelectedFile(file);
    setPreviewUrl(previewData);
    setUploadResult(null);
    setShowCamera(false); //Kamerayƒ± kapat
  }

  //Dosya y√ºkleme butonƒ±na tƒ±klanƒ±nca √ßalƒ±≈üacak fonksiyon:
  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file first.");
      return;
    }

    setIsLoading(true);
    setUploadResult(null);

    //Backende dosya g√∂ndermek i√ßin formData kullanma:
    const formData = new FormData();
    formData.append("document" , selectedFile);

    try {

      console.log("Uploading file...", selectedFile.name);

      //Axios ile backend APIsine POST isteƒüi g√∂nderme:
      const response = await axios.post("http://localhost:5000/api/upload" , formData , {
        headers: {
          "Content-Type" : "multipart/form-data"
        },
      });


      setUploadResult(response.data);
      console.log("Upload successful:", response.data);

    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadResult({
        success: false,
        error: "Error uploading file" + error.message
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Dosya se√ßme butonuna tƒ±klanƒ±nca √ßalƒ±≈üacak fonksiyon:
  const handleSelectClick = () => {
    fileInputRef.current.click();
  };

  //Kamera A√ßma butonu:
  const handleCameraClick = () => {
    setShowCamera(true);
  };

  //Metin Iyile≈ütirme Fonksiyonu:
  const enhanceText = async () => {
  if (!uploadResult || !uploadResult.ocr || !uploadResult.ocr.text) {
    alert("Please upload a file and complete OCR first");
    return;
  }

  setIsEnhancing(true);

  try {
    const response = await axios.post("http://localhost:5000/api/enhance-text", {
      text: uploadResult.ocr.text
    });

    const enhancement = response.data.enhancement;

    // Eƒüer metin zaten temizse kullanƒ±cƒ±ya d√ºr√ºst ol
    if (!enhancement || enhancement.enhanced === uploadResult.ocr.text) {
      alert("Text is already clean. No improvement needed.");
      setIsEnhancing(false);
      return;
    }

    // State'e kaydet
    setUploadResult(prev => ({
      ...prev,
      enhancement
    }));

    setShowEnhancedText(true);

  } catch (error) {
    console.error("Text enhancement error:", error);
    alert("Text enhancement failed: " + error.message);
  } finally {
    setIsEnhancing(false);
  }
};


  //Orijinal ve iyile≈ütirilmi≈ü metin arasƒ± ge√ßi≈ü. 
  const toggleTextVersion = () => {
  setShowEnhancedText(!showEnhancedText);
  };

  // PDF Olu≈üturma fonksiyonu:
  const generatePDF = async (type = 'simple') => {
    if (!uploadResult || !uploadResult.ocr || !uploadResult.ocr.text) {
      alert('Please upload a file and complete OCR process first.');
      return;
    }

    setIsGeneratingPDF(true);

    try {
      let response;

      if (type === 'simple') {
        // Simple PDF
        response = await axios.post('http://localhost:5000/api/generate-pdf', {
          ocrText: uploadResult.ocr.text,
          title: `Scanned: ${uploadResult.file.originalname}`
        });
      }


      setPdfInfo(response.data.pdf);
      console.log('PDF created:', response.data.pdf);

    } catch (error) {
      console.error('PDF generation error:', error);
      alert('PDF creation error: ' + error.message);
    } finally {
      setIsGeneratingPDF(false);
    }
  };

  // PDF download function
  const downloadPDF = () => {
    if (pdfInfo && pdfInfo.pdfUrl) {
      window.open(`http://localhost:5000${pdfInfo.pdfUrl}`, '_blank');
    }
  };

return (
    <div className="App">
      {/* YENƒ∞ HEADER - Modern ve temiz */}
      <header className="app-header">
        <div className="container">
          <h1>üìÑ Document Scanner Pro</h1>
          <p className="text-muted" style={{color: 'rgba(255,255,255,0.9)'}}>
            Upload, scan, and enhance documents with AI-powered OCR
          </p>
        </div>
      </header>

      <main className="main-content">
        <div className="container">

          {/* UPLOAD SECTION - Yeniden tasarlandƒ± */}
          <section className="card upload-section animate-fadeIn">
            <div className="card-header">
              <h2>Upload Document</h2>
              <p className="text-muted">Choose a file or capture using camera</p>
            </div>

            {/* Gizli file input */}
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileSelect}
              accept="image/*,.pdf"
              style={{display: "none"}}
            />

            {/* Button Group - Modern tasarƒ±m */}
            <div className="flex gap-4 mb-4" style={{flexWrap: 'wrap'}}>
              <button 
                className="btn btn-primary"
                onClick={handleSelectClick}
              >
                üìÅ Choose File
              </button>

              <button 
                className="btn btn-outline-primary"
                onClick={handleCameraClick}
              >
                üì∏ Open Camera
              </button>
            </div>

            {/* Selected File Info - Daha temiz g√∂r√ºn√ºm */}
            {selectedFile && (
              <div className="card" style={{background: 'var(--color-gray-50)'}}>
                <div className="flex items-center justify-between">
                  <div>
                    <strong>üìÑ {selectedFile.name}</strong>
                    <p className="text-muted m-0" style={{fontSize: '0.875rem'}}>
                      Size: {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                    </p>
                  </div>
                  <button 
                    className="btn btn-sm btn-secondary"
                    onClick={() => {
                      setSelectedFile(null);
                      setPreviewUrl(null);
                    }}
                  >
                    ‚úï
                  </button>
                </div>
              </div>
            )}
          </section>

          {/* PREVIEW SECTION - Geli≈ütirilmi≈ü */}
          {previewUrl && (
            <section className="card preview-section animate-fadeIn">
              <h3>Document Preview</h3>

              <div className="mb-4" style={{maxWidth: '500px', margin: '0 auto'}}>
                <img
                  src={previewUrl}
                  alt="Document preview"
                  className="preview-image"
                />
              </div>

              <button
                className={`btn btn-success btn-lg ${isLoading ? 'btn-loading' : ''}`}
                onClick={handleUpload}
                disabled={isLoading}
                style={{minWidth: '200px'}}
              >
                {isLoading ? '' : 'üöÄ Upload & Scan'}
              </button>

              {isLoading && (
                <div className="mt-4 text-center">
                  <div className="loading-spinner" style={{margin: '0 auto'}}></div>
                  <p className="text-muted mt-2">Processing your document...</p>
                </div>
              )}
            </section>
          )}

          {/* RESULTS SECTION - Yeniden yapƒ±landƒ±rƒ±lacak */}
          {uploadResult && (
            <ResultsSection 
              uploadResult={uploadResult}
              isEnhancing={isEnhancing}
              showEnhancedText={showEnhancedText}
              pdfInfo={pdfInfo}
              isGeneratingPDF={isGeneratingPDF}
              onEnhanceText={enhanceText}
              onToggleText={toggleTextVersion}
              onGeneratePDF={generatePDF}
              onDownloadPDF={downloadPDF}
            />
          )}

          {/* CAMERA COMPONENT */}
          {showCamera && (
            <Camera
              onCapture={handleCameraCapture}
              onClose={() => setShowCamera(false)}
            />
          )}
        </div>
      </main>

      {/* FOOTER - Profesyonel g√∂r√ºn√ºm i√ßin */}
      <footer className="text-center p-6 text-muted" style={{borderTop: '1px solid var(--color-gray-200)'}}>
        <div className="container">
          <p>Document Scanner Pro ¬© {new Date().getFullYear()} ‚Ä¢ AI-Powered Document Processing</p>
        </div>
      </footer>
    </div>
  );
}

