// src/App.js - YENƒ∞ VERSƒ∞YON (Kƒ±smi d√ºzenleme)

import React, {useState, useRef} from "react";
import axios from "axios";
import "./App.css"; // Yeni CSS'imiz
import Camera from "./components/Camera";

function App() {
  // State'ler aynƒ± kalacak
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [uploadResult, setUploadResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showCamera, setShowCamera] = useState(false);
  const [pdfInfo, setPdfInfo] = useState(null);
  const [isGeneratingPDF, setIsGeneratingPDF] = useState(false);
  const [showEnhancedText, setShowEnhancedText] = useState(false);
  const [isEnhancing, setIsEnhancing] = useState(false);

  const fileInputRef = useRef();

  // Fonksiyonlar aynƒ± kalacak...
  // handleFileSelect, handleCameraCapture, handleUpload vb.

  return (
    <div className="App">
      {/* YENƒ∞ HEADER - Modern ve temiz */}
      <header className="app-header">
        <div className="container">
          <h1>üìÑ Document Scanner Pro</h1>
          <p className="text-muted" style={{color: 'rgba(255,255,255,0.9)'}}>
            Upload, scan, and enhance documents with AI-powered OCR
          </p>
        </div>
      </header>

      <main className="main-content">
        <div className="container">

          {/* UPLOAD SECTION - Yeniden tasarlandƒ± */}
          <section className="card upload-section animate-fadeIn">
            <div className="card-header">
              <h2>Upload Document</h2>
              <p className="text-muted">Choose a file or capture using camera</p>
            </div>

            {/* Gizli file input */}
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileSelect}
              accept="image/*,.pdf"
              style={{display: "none"}}
            />

            {/* Button Group - Modern tasarƒ±m */}
            <div className="flex gap-4 mb-4" style={{flexWrap: 'wrap'}}>
              <button 
                className="btn btn-primary"
                onClick={handleSelectClick}
              >
                üìÅ Choose File
              </button>

              <button 
                className="btn btn-outline-primary"
                onClick={handleCameraClick}
              >
                üì∏ Open Camera
              </button>
            </div>

            {/* Selected File Info - Daha temiz g√∂r√ºn√ºm */}
            {selectedFile && (
              <div className="card" style={{background: 'var(--color-gray-50)'}}>
                <div className="flex items-center justify-between">
                  <div>
                    <strong>üìÑ {selectedFile.name}</strong>
                    <p className="text-muted m-0" style={{fontSize: '0.875rem'}}>
                      Size: {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                    </p>
                  </div>
                  <button 
                    className="btn btn-sm btn-secondary"
                    onClick={() => {
                      setSelectedFile(null);
                      setPreviewUrl(null);
                    }}
                  >
                    ‚úï
                  </button>
                </div>
              </div>
            )}
          </section>

          {/* PREVIEW SECTION - Geli≈ütirilmi≈ü */}
          {previewUrl && (
            <section className="card preview-section animate-fadeIn">
              <h3>Document Preview</h3>

              <div className="mb-4" style={{maxWidth: '500px', margin: '0 auto'}}>
                <img
                  src={previewUrl}
                  alt="Document preview"
                  className="preview-image"
                />
              </div>

              <button
                className={`btn btn-success btn-lg ${isLoading ? 'btn-loading' : ''}`}
                onClick={handleUpload}
                disabled={isLoading}
                style={{minWidth: '200px'}}
              >
                {isLoading ? '' : 'üöÄ Upload & Scan'}
              </button>

              {isLoading && (
                <div className="mt-4 text-center">
                  <div className="loading-spinner" style={{margin: '0 auto'}}></div>
                  <p className="text-muted mt-2">Processing your document...</p>
                </div>
              )}
            </section>
          )}

          {/* RESULTS SECTION - Yeniden yapƒ±landƒ±rƒ±lacak */}
          {uploadResult && (
            <ResultsSection 
              uploadResult={uploadResult}
              isEnhancing={isEnhancing}
              showEnhancedText={showEnhancedText}
              pdfInfo={pdfInfo}
              isGeneratingPDF={isGeneratingPDF}
              onEnhanceText={enhanceText}
              onToggleText={toggleTextVersion}
              onGeneratePDF={generatePDF}
              onDownloadPDF={downloadPDF}
            />
          )}

          {/* CAMERA COMPONENT */}
          {showCamera && (
            <Camera
              onCapture={handleCameraCapture}
              onClose={() => setShowCamera(false)}
            />
          )}
        </div>
      </main>

      {/* FOOTER - Profesyonel g√∂r√ºn√ºm i√ßin */}
      <footer className="text-center p-6 text-muted" style={{borderTop: '1px solid var(--color-gray-200)'}}>
        <div className="container">
          <p>Document Scanner Pro ¬© {new Date().getFullYear()} ‚Ä¢ AI-Powered Document Processing</p>
        </div>
      </footer>
    </div>
  );
}

// ResultsSection bile≈üenini daha sonra ayƒ±racaƒüƒ±z
function ResultsSection({ 
  uploadResult, 
  isEnhancing, 
  showEnhancedText, 
  pdfInfo, 
  isGeneratingPDF,
  onEnhanceText,
  onToggleText,
  onGeneratePDF,
  onDownloadPDF
}) {
  if (!uploadResult.success) {
    return (
      <div className="card" style={{borderLeft: '4px solid var(--color-danger)'}}>
        <h3 className="text-danger">‚ùå Upload Failed</h3>
        <p>{uploadResult.error}</p>
      </div>
    );
  }

  return (
    <section className="card result-section animate-fadeIn">
      <h2>üìä Analysis Results</h2>

      {/* File Info */}
      <div className="card mb-4" style={{background: 'var(--color-gray-50)'}}>
        <div className="grid grid-cols-2 gap-4">
          <div>
            <strong>File Name</strong>
            <p className="m-0">{uploadResult.file.originalname}</p>
          </div>
          <div>
            <strong>File Size</strong>
            <p className="m-0">{(uploadResult.file.size / 1024 / 1024).toFixed(2)} MB</p>
          </div>
        </div>
      </div>

      {/* Image Analysis - DEVAM EDECEK... */}
      {uploadResult.analysis && (
        <AnalysisSection analysis={uploadResult.analysis} />
      )}

      {/* OCR Results - DEVAM EDECEK... */}
      {uploadResult.ocr && (
        <OCRSection 
          ocr={uploadResult.ocr}
          enhancement={uploadResult.enhancement}
          isEnhancing={isEnhancing}
          showEnhancedText={showEnhancedText}
          onEnhanceText={onEnhanceText}
          onToggleText={onToggleText}
        />
      )}

      {/* PDF Export - DEVAM EDECEK... */}
      {uploadResult.ocr?.success && uploadResult.ocr.text && (
        <PDFSection 
          pdfInfo={pdfInfo}
          isGeneratingPDF={isGeneratingPDF}
          onGeneratePDF={onGeneratePDF}
          onDownloadPDF={onDownloadPDF}
        />
      )}
    </section>
  );
}

// Diƒüer bile≈üenler buraya gelecek...
// AnalysisSection, OCRSection, PDFSection vs.

export default App;
